function remove_jetpack_menu_item() {

	if( class_exists( 'Jetpack' ) && !current_user_can( 'manage_options' ) ) {
		remove_menu_page( 'jetpack' );
	}
}
add_action( 'admin_init', 'remove_jetpack_menu_item' );

function remove_admin_bar_jetpack() {

    global $wp_admin_bar;
    $wp_admin_bar->remove_menu( 'notes' );
}
add_action( 'wp_before_admin_bar_render', 'remove_admin_bar_jetpack', 0 );
